INSERT INTO `product` (`name`, `image_url`, `price`, `old_price`, `description`, `created_at`, `modified_at`) VALUES
('Chuột Gaming Durgod V90 Pro - 10.000 DPI', 'https://clickzone.s3.ap-southeast-2.amazonaws.com/2037c32f-d59c-4abd-9e68-2cfdd904aee9.jpeg', 245000, 490000, '<h3>- Mắt đọc cảm biến PMW3325 đem lại độ chính xác gần như tuyệt đối, thường được dùng ở các dòng chuột trung cấp.</h3><h3>- Hệ thống led RGB 16,8 triệu màu.</h3><h3>- Phần mềm hỗ trợ tùy chỉnh giao diện tiếng Anh dễ sử dụng.</h3><h3>- Thiết kế Ergonomic (công thái học) giúp bạn sử dụng lâu mà vẫn thoải mái.</h3><h3>- Switch Huano cho cảm giác bấm tốt, độ bền 20 triệu lần.</h3><h3>- Dây cáp bọc vải dù chắc chắn.</h3><h3>- DPI max 10.000.</h3><h3>- Bảo hành chính hãng Ninza 2 năm (1 đổi 1 trong 10 ngày đầu nếu lỗi kỹ thuật).</h3>', '2023-04-15 07:37:25', '2023-04-15 07:37:25'),
('Bàn phím cơ Dareu EK87 V2 (Multi-Led)', 'https://clickzone.s3.ap-southeast-2.amazonaws.com/50c63f68-ddf4-4107-a03e-587dc1c02f96.jpeg', 495000, 595000, '<h3>- Bản nâng cấp của EK87 cũ với màu sắc đẹp hơn, nhiều hiệu ứng hơn, keycap được làm lại, giá ko đổi.</h3><h3>- Led Rainbow Area 6 hiệu ứng, hỗ trợ thêm 5 profile gaming tự setup.</h3><h3>- Switch D công nghệ độc quyền của hãng với độ bền 60 triệu lần.</h3><h3>- Dây cáp cao su, dài 1,5m.</h3><h3>- Sử dụng stab kiểu Cherry giúp bạn dễ thay keycap.</h3><h3>- Bảo hành chính hãng 2 năm + 1 năm tại shop (1 đổi 1 trong 10 ngày đầu nếu lỗi kỹ thuật).</h3>', '2023-04-15 07:38:12', '2023-04-15 07:38:12'),
('Bàn phím cơ RK918 White - Kèm kê tay', 'https://clickzone.s3.ap-southeast-2.amazonaws.com/92fe9ef4-06aa-46d3-a5ed-c6443286ea19.jpeg', 1090000, NULL, '<h3>- Phiên bản 2022 mới nhất: Stab xịn hơn, êm và chắc chắn; font chữ đẹp hơn; có sách HD đa ngôn ngữ, tùy đợt hàng có tặng kèm keypuller thép.</h3><h3>- Sử dụng switch RK được cải tiến, cảm giác bấm mềm hơn, độ bền 50 triệu lần bấm.</h3><h3>- Led RGB 16,8 triệu màu nhiều hiệu ứng.</h3><h3>- Phần mềm tùy chỉnh giao diện tiếng Anh dễ sử dụng (chỉ hỗ trợ HĐH Window).</h3><h3>- Tích hợp led gầm RGB siêu sang, siêu sáng.</h3><h3>- Keycap ABS double shot ko mờ chữ.</h3><h3>- Plate hợp kim cắt CNC cao cấp.</h3><h3>- Bảo hành 2 năm (1 đổi 1 trong 10 ngày đầu nếu lỗi).</h3>', '2023-04-15 07:39:22', '2023-04-15 07:39:22'),
('Bàn phím cơ Dareu EK807G - Wireless 2.4ghz', 'https://clickzone.s3.ap-southeast-2.amazonaws.com/89135cbd-a31b-4ac7-a426-5d848a92ea18.jpeg', 545000, 645000, '<h3>- Kết nối không dây wireless 2.4ghz, dùng 2 pin AAA cho thời gian sử dụng ~ 6 tháng.</h3><h3>- Switch D (Dareu) chính hãng độ bền cao.</h3><h3>- Keycap ABS double shot.</h3><h3>- KO có led.</h3><h3>- Layout 87 (TKL) nhỏ gọn.</h3><h3>- Bảo hành chính hãng 2 năm (1 đổi 1 trong 10 ngày đầu với lỗi kỹ thuật).</h3>', '2023-04-15 07:40:14', '2023-04-15 07:40:14'),
('Bàn phím cơ Keycool GZ68 - Pudding (3 Modes, Hotswap) modified', 'https://clickzone.s3.ap-southeast-2.amazonaws.com/07f16826-3110-41eb-b512-a9d9045afc06.jpeg', 1390000, NULL, '<h3>- Kết nối 3 chế độ: Bluetooth 5.0, wireless 2.4ghz + cáp.</h3><h3>- Keycap PBT pudding độc đáo, OEM profile.</h3><h3>- HUANO switch, mạch hotswap giúp thay đổi switch dễ dàng.</h3><h3>- Led RGB đa hiệu ứng.</h3><h3>- Có phần mềm tùy chỉnh giao diện tiếng Anh.</h3><h3>- Hỗ trợ layout MacOs.</h3><h3>- Tích hợp sẵn foam PCB (tiêu âm).</h3><h3>- Pin 2700 mAh cho thời gian sử dụng khoảng 70 tiếng (KO led).</h3><h3>- Phụ kiện: Cáp, switch puller, keypuller, sách.</h3><h3>- Bảo hành 2 năm (1 đổi 1 trong 10 ngày đầu cho lỗi kỹ thuật).</h3>', '2023-04-15 07:41:15', '2023-04-15 07:41:15'),
('Bộ Keycap Son Goku - Super Saiyan Blue (PBT, Cherry Profile)', 'https://clickzone.s3.ap-southeast-2.amazonaws.com/8c6e7253-61be-4e5f-aa3f-742bef01797e.jpeg', 195000, NULL, '<h3>- Chất liệu: PBT.</h3><h3>- Công nghệ in: Dye-sub 5 mặt.</h3><h3>- Profile: Cherry, dày 1.3mm.</h3><h3>- Số phím: 15.</h3>', '2023-04-15 07:42:02', '2023-04-15 07:42:02');
